import React, { Fragment } from 'react';
import '../../../components/styles/Demo.css';
import Tabs from '../../../tabs/Tabs.js';
import {Link} from 'react-router-dom';
import Fade from 'react-reveal/Fade';
import Quiz from '../../../components/quiz/Quiz.js';

import fire from '../../../config/Fire';
import Login from '../../../components/Login.js';

class Unit6Lesson7 extends React.Component {
    constructor(props) {
        super(props);
        this.logout = this.logout.bind(this);
        this.state = ({
            user: null,
          });
          this.authListener = this.authListener.bind(this);
    }
    logout(){
        fire.auth().signOut();
    }
    componentDidMount(){
        this.authListener();
      }
    
      authListener() {
        fire.auth().onAuthStateChanged((user) => {
      //login
          if (user) {
            this.setState({user});
            localStorage.setItem('user', user.uid);
          } 
      //logout
          else {
            this.setState({ user:null});
            localStorage.removeItem('user');
          }
        });
      }
    render() {
        return (
            <div>
                {this.state.user ? (<Lesson />) : (<Login />)}
            </div>
        )
      }
}
class Lesson extends React.Component {
    render() {
        return (
            <div class = "container">

                <div class = "split left">
                    <div class = "lesson_video">
                        <iframe src="https://www.youtube.com/embed/OB7qpEFDUzs?rel=0" title="Video" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen="true"></iframe>
                    </div> 
                </div>
                <div class = "split right">
                    <div class = "lesson_content">
                        <div class = "lesson_title grow">Special Methods</div>
                        <div class = "tabs">
                            <Tabs>
                                
                                <div class="tab_label" label="Supplemental">
                                    <div class="tab_header">Object Oriented Programming</div>
                                    <div class="paragraph">We’re finally here! Object Oriented Programming also known as OOP is the next step of becoming a more proficient Python programmer. It may be a little difficult at the start but with practice, the concepts of OOP will fall into place. Take your time, work with the code, and you’ll know about OOP in no time.</div>
                                    <div class="paragraph">We’ll begin by recalling Basic Python Objects. For example:</div>
                                    <div class = "code_block">
                                        <div><div class="blue">def</div> <div class="purp">name_of_function</div>(arg1,arg2):</div>
                                    </div>
                                    <div class="paragraph">In this supplemental, you’ll learn about how to create an Object type such as a list. Let’s begin by exploring some Objects within Python.</div>
                                    <div class="tab_header">Objects</div>
                                    <div class="paragraph">One thing that needs to be stressed here is that in Python, everything is an object. A good way of thinking about this is when we use the type function. By using type() to check the type of object something is, it reveals that everything within Python is inherently an object. Take a look at the example:</div>
                                    <div class = "code_block">
                                        <div><div class="blue">def</div> <div class="purp">name_of_function</div>(arg1,arg2):</div>
                                    </div>
                                    <div class="paragraph">This is the very basics of OOP in Python. In the next lesson, we’ll be going in depth into how we can create our own unique methods!</div>
                                </div>

                                <div class="tab_label" label="Quiz">
                                <Fragment>
                                    <Quiz 
                                        quizdata={
                                            {
                                              question: "How can you use built-in python methods with your own user defined methods?",
                                              FindAnswer:["By chaining comparison operators", "By importing a Python library", "By using special methods", "You can freely use built-in python methods with user defined methods"], 
                                              rightAnswer:"By using special methods"
                                            }
                                        }
                                    />
                                    <Quiz 
                                        quizdata={
                                            {
                                                question: "What is the special method for strings?",
                                                FindAnswer:[ "strings.method()", "__str__", "str", "strings()"], 
                                                rightAnswer:"__str__"
                                            }
                                        }
                                    />
                                    <Quiz 
                                        quizdata={
                                            {
                                                question: "What is the special method for length?",
                                                FindAnswer:[ "There is no such special method", "lengths.call()", "__lengths__", "__len__"], 
                                                rightAnswer:"__len__"
                                            }
                                        }
                                    />
                                </Fragment> 
                                </div>

                                <div class="tab_label" label="Assignment">
                                    <div class = "tab_header">Task</div>
                                    <div class='paragraph'>Create a user defined method that takes your name and your favorite movie. Create an instance of your class and try to print your name and your favorite book in this format:</div>    
                                    <div class='paragraph_NI bold'>‘My name is yourName and my favorite movie is favMovie’</div>
                                    <div class='paragraph'>Use the special method for strings to achieve this. </div>
                                    <iframe height="400px" width="100%" src="https://repl.it/@SursumEducation/Unit6Lesson5?lite=true" title="Code" scrolling="no" frameborder="no" allowtransparency="true" allowfullscreen="true" sandbox="allow-forms allow-pointer-lock allow-popups allow-same-origin allow-scripts allow-modals"></iframe>
                                </div>
                                
                            </Tabs>
                        </div>
                    </div>
                </div>

                <div class = "bottom_section">
                    <Fade bottom><div><div class ="lesson_backbutton"><Link to='/Python'><button class="grow">View Other Lessons</button></Link></div></div></Fade>
                </div> 

            </div>
        )
      }
}

export default Unit6Lesson7;
